import React, { useState, useEffect } from 'react';
import {
  Box,
  VStack,
  HStack,
  Text,
  Button,
  Badge,
  useToast,
  IconButton,
  Heading,
} from '@chakra-ui/react';
import { DeleteIcon, CheckIcon } from '@chakra-ui/icons';
import axios from 'axios';

function HabitList() {
  const [habits, setHabits] = useState([]);
  const toast = useToast();

  useEffect(() => {
    fetchHabits();
  }, []);

  const fetchHabits = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/habits');
      setHabits(response.data);
    } catch (error) {
      toast({
        title: 'Error fetching habits',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  const handleComplete = async (id) => {
    try {
      await axios.post(`http://localhost:5000/api/habits/${id}/complete`);
      fetchHabits();
      toast({
        title: 'Habit marked as complete',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      toast({
        title: 'Error marking habit as complete',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/habits/${id}`);
      fetchHabits();
      toast({
        title: 'Habit deleted',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      toast({
        title: 'Error deleting habit',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    }
  };

  return (
    <Box>
      <Heading mb={6}>Your Habits</Heading>
      <VStack spacing={4} align="stretch">
        {habits.map((habit) => (
          <Box
            key={habit._id}
            p={4}
            bg="white"
            borderRadius="lg"
            boxShadow="sm"
          >
            <HStack justify="space-between">
              <Box>
                <Text fontSize="lg" fontWeight="bold">
                  {habit.name}
                </Text>
                <Text color="gray.600" fontSize="sm">
                  {habit.description}
                </Text>
                <Badge colorScheme="blue" mt={2}>
                  {habit.frequency}
                </Badge>
              </Box>
              <HStack>
                <IconButton
                  icon={<CheckIcon />}
                  colorScheme="green"
                  onClick={() => handleComplete(habit._id)}
                  aria-label="Mark as complete"
                />
                <IconButton
                  icon={<DeleteIcon />}
                  colorScheme="red"
                  onClick={() => handleDelete(habit._id)}
                  aria-label="Delete habit"
                />
              </HStack>
            </HStack>
          </Box>
        ))}
      </VStack>
    </Box>
  );
}

export default HabitList;
