import React from 'react';
import {
  Box,
  Container,
  Heading,
  Text,
  Button,
  VStack,
  SimpleGrid,
  Icon,
  useColorModeValue,
} from '@chakra-ui/react';
import { motion } from 'framer-motion';
import { FaCheckCircle, FaChartBar, FaCalendarAlt, FaPlus } from 'react-icons/fa';

const MotionBox = motion(Box);

const Feature = ({ title, text, icon }) => {
  return (
    <MotionBox
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      p={6}
      bg={useColorModeValue('white', 'gray.800')}
      rounded="xl"
      borderWidth="2px"
      borderColor="yellow.400"
      shadow="lg"
      _hover={{ shadow: 'xl' }}
    >
      <VStack spacing={4} align="start">
        <Icon as={icon} w={10} h={10} color="yellow.500" />
        <Heading size="md">{title}</Heading>
        <Text color={useColorModeValue('gray.600', 'gray.300')}>{text}</Text>
      </VStack>
    </MotionBox>
  );
};

const Home = () => {
  return (
    <Box>
      {/* Hero Section */}
      <Box
        bg={useColorModeValue('yellow.50', 'gray.900')}
        py={20}
        borderBottomWidth="2px"
        borderColor="yellow.200"
      >
        <Container maxW="container.xl">
          <VStack spacing={8} textAlign="center">
            <MotionBox
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ duration: 0.5 }}
              fontSize="8xl"
            >
              🐰
            </MotionBox>
            <Heading
              size="2xl"
              bgGradient="linear(to-r, yellow.400, yellow.600)"
              bgClip="text"
              textShadow="2px 2px 4px rgba(0,0,0,0.1)"
            >
              Welcome to HabitRabbit!
            </Heading>
            <Text fontSize="xl" maxW="2xl">
              Your friendly companion for building and maintaining good habits. Let's hop into a better future together!
            </Text>
            <Button
              as={motion.button}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              size="lg"
              colorScheme="yellow"
              onClick={() => window.location.href = '/add'}
            >
              Start Your Journey
            </Button>
          </VStack>
        </Container>
      </Box>

      {/* Features Section */}
      <Container maxW="container.xl" py={20}>
        <VStack spacing={12}>
          <Heading
            textAlign="center"
            bgGradient="linear(to-r, yellow.400, yellow.600)"
            bgClip="text"
          >
            How HabitRabbit Helps You
          </Heading>
          <SimpleGrid columns={{ base: 1, md: 2, lg: 4 }} spacing={8}>
            <Feature
              icon={FaCheckCircle}
              title="Track Daily Habits"
              text="Mark your habits as complete and watch your progress grow day by day."
            />
            <Feature
              icon={FaChartBar}
              title="View Statistics"
              text="Get insights into your habit completion rates and track your improvement."
            />
            <Feature
              icon={FaCalendarAlt}
              title="Calendar View"
              text="Visualize your habit completion history with our interactive calendar."
            />
            <Feature
              icon={FaPlus}
              title="Add New Habits"
              text="Easily create and customize new habits to build your perfect routine."
            />
          </SimpleGrid>
        </VStack>
      </Container>

      {/* Call to Action */}
      <Box
        bg={useColorModeValue('yellow.50', 'gray.900')}
        py={20}
        borderTopWidth="2px"
        borderColor="yellow.200"
      >
        <Container maxW="container.xl">
          <VStack spacing={8} textAlign="center">
            <Heading
              size="xl"
              bgGradient="linear(to-r, yellow.400, yellow.600)"
              bgClip="text"
            >
              Ready to Start Your Habit Journey?
            </Heading>
            <Text fontSize="lg" maxW="2xl">
              Join thousands of users who are already building better habits with HabitRabbit.
            </Text>
            <Button
              as={motion.button}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              size="lg"
              colorScheme="yellow"
              onClick={() => window.location.href = '/add'}
            >
              Get Started Now
            </Button>
          </VStack>
        </Container>
      </Box>
    </Box>
  );
};

export default Home;
