import React from 'react';
import { Box, Flex, Link, Heading, useColorModeValue } from '@chakra-ui/react';
import { Link as RouterLink } from 'react-router-dom';

function Navbar() {
  const bgColor = useColorModeValue('yellow.500', 'yellow.600');
  const hoverColor = useColorModeValue('yellow.100', 'yellow.200');
  
  return (
    <Box bg={bgColor} px={4} py={3} boxShadow="md">
      <Flex maxW="container.xl" mx="auto" align="center" justify="space-between">
        <Heading 
          size="md" 
          color="blackAlpha.900"
          textShadow="1px 1px 2px rgba(0,0,0,0.1)"
        >
          Habit Tracker
        </Heading>
        <Flex gap={6}>
          <Link 
            as={RouterLink} 
            to="/" 
            color="blackAlpha.900" 
            fontWeight="medium"
            _hover={{ 
              textDecoration: 'none', 
              color: 'blackAlpha.700',
              transform: 'translateY(-1px)',
              transition: 'all 0.2s'
            }}
          >
            Habits
          </Link>
          <Link 
            as={RouterLink} 
            to="/add" 
            color="blackAlpha.900" 
            fontWeight="medium"
            _hover={{ 
              textDecoration: 'none', 
              color: 'blackAlpha.700',
              transform: 'translateY(-1px)',
              transition: 'all 0.2s'
            }}
          >
            Add Habit
          </Link>
          <Link 
            as={RouterLink} 
            to="/stats" 
            color="blackAlpha.900" 
            fontWeight="medium"
            _hover={{ 
              textDecoration: 'none', 
              color: 'blackAlpha.700',
              transform: 'translateY(-1px)',
              transition: 'all 0.2s'
            }}
          >
            Statistics
          </Link>
        </Flex>
      </Flex>
    </Box>
  );
}

export default Navbar;
